
public class solidrectangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    //outer loop
		int m = 3;
		int n=4;
    for(int i=0;i<=m;i++)
    {
    	//inner loop
    	for(int j=0;j<=n;j++)
    	{
    		System.out.print("*");
        }
    
        System.out.println();
    }
}

	
}
